const cms = require('cms');
module.exports =  async function(user) {
  let stores = (await cms.getModel('Store').find({groups: {
    $elemMatch: {$in: user.storeGroups}
  }}).select('_id').lean()).map(r => r._id);

  return {
    find: {
      store: {
        $in: stores
      }
    }
  }
}
